// App.js
import React, { useState } from 'react';
import './App.css';

const questions = [
  "I have felt cheerful and in good spirits.",
  "I have felt calm and relaxed.",
  "I have felt active and vigorous.",
  "I woke up feeling fresh and rested.",
  "My daily life has been filled with things that interest me."
];

function App() {
  const [answers, setAnswers] = useState(Array(5).fill(null));
  const [showResult, setShowResult] = useState(false);

  const handleAnswer = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const calculateScore = () => {
    const sum = answers.reduce((acc, curr) => acc + curr, 0);
    return sum * 4;
  };

  const getExplanation = (score) => {
    if (score <= 20) {
      return (
        <>
          <p>Your well-being score indicates very poor well-being.</p>
          <p><strong>Recommendations:</strong></p>
          <ul>
            <li>Seek immediate professional help from a mental health expert.</li>
            <li>Reach out to trusted friends or family members for support.</li>
            <li>Consider joining a support group for emotional assistance.</li>
          </ul>
        </>
      );
    } else if (score <= 50) {
      return (
        <>
          <p>Your well-being score indicates poor well-being.</p>
          <p><strong>Recommendations:</strong></p>
          <ul>
            <li>Consult with a healthcare professional for personalized advice.</li>
            <li>Engage in regular physical exercise.</li>
            <li>Practice mindfulness or relaxation techniques.</li>
            <li>Establish a regular sleep routine.</li>
          </ul>
        </>
      );
    } else if (score <= 75) {
      return (
        <>
          <p>Your well-being score indicates moderate well-being.</p>
          <p><strong>Recommendations:</strong></p>
          <ul>
            <li>Continue practicing self-care activities such as exercise and relaxation.</li>
            <li>Maintain social connections and spend time with loved ones.</li>
            <li>Set aside time for hobbies and activities you enjoy.</li>
            <li>Consider keeping a journal to reflect on positive experiences.</li>
          </ul>
        </>
      );
    } else if (score <= 95) {
      return (
        <>
          <p>Your well-being score indicates good well-being.</p>
          <p><strong>Recommendations:</strong></p>
          <ul>
            <li>Keep up the good work with your current healthy habits.</li>
            <li>Explore new activities or hobbies that interest you.</li>
            <li>Share your well-being strategies with friends and family.</li>
            <li>Continue to monitor your well-being and adjust as needed.</li>
          </ul>
        </>
      );
    } else {
      return (
        <>
          <p>Your well-being score indicates excellent well-being.</p>
          <p><strong>Recommendations:</strong></p>
          <ul>
            <li>Continue your great habits and maintain your current lifestyle.</li>
            <li>Consider mentoring others on well-being practices.</li>
            <li>Explore further personal growth opportunities.</li>
            <li>Stay proactive in monitoring and maintaining your well-being.</li>
          </ul>
        </>
      );
    }
  };

  const handleSubmit = () => {
    if (answers.includes(null)) {
      alert("Please answer all questions before submitting.");
    } else {
      setShowResult(true);
    }
  };

  const restartQuiz = () => {
    setAnswers(Array(5).fill(null));
    setShowResult(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>WHO-5 Well-being Index</h1>
        {!showResult ? (
          <div className="question-container">
            <p className="instruction">Rate Your Well-being Over the Past Two Weeks:</p>
            {questions.map((question, index) => (
              <div key={index} className="question">
                <p className="question-text">{question}</p>
                <div className="answers">
                  {[0, 1, 2, 3, 4, 5].map((value) => (
                    <button
                      key={value}
                      className={`answer-button ${answers[index] === value ? 'selected' : ''}`}
                      onClick={() => handleAnswer(index, value)}
                    >
                      {value}
                    </button>
                  ))}
                </div>
              </div>
            ))}
            <button className="submit-button" onClick={handleSubmit}>Check my mental health</button>
          </div>
        ) : (
          <div className="result-container">
            <p className="result-text">Your well-being score is:</p>
            <p className="score">{calculateScore()}</p>
            <div className="explanation">{getExplanation(calculateScore())}</div>
            <button className="restart-button" onClick={restartQuiz}>Restart Quiz</button>
          </div>
        )}
      </header>
      <footer className="footer">
        <p>&copy; 2024 moabmo</p>
      </footer>
    </div>
  );
}

export default App;
